import React from "react";
import MovieList from "./MovieList";
import useFetch from "./useFetch";
import SearchBox from "./SearchBox";
import { useState, useEffect } from "react";

const Movies = () => {
  const [search, setSearchValue] = useState("");
  const {
    error,
    isPending,
    data: movies,
  } = useFetch(`http://localhost:8000/movies/title?title=${search}`);

  return (
    <>
      <SearchBox search={search} setSearchValue={setSearchValue} />
      <div className="home">
        {error && <div>{error}</div>}
        {isPending && <div>Loading...</div>}
        <h2>Featured movies</h2>
        {movies && (
          <MovieList
            className="movie-center"
            movies={movies.filter(
              (movie) => movie.Type === "movie" && movie.featured === "yes"
            )}
          />
        )}
        {error && <div>{error}</div>}
        {isPending && <div>Loading...</div>}
        <h2 className="second-title">All Movies</h2>
        {movies && (
          <MovieList
            movies={movies.filter((movie) => movie.type === "movie")}
          />
        )}
      </div>
    </>
  );
};

export default Movies;
