import React, { useState } from "react";
import MovieList from "./MovieList";
import useFetch from "./useFetch";
const TvShows = () => {
  const [search, setSearchValue] = useState("");
  const {
    error,
    isPending,
    data: movies,
  } = useFetch(`http://localhost:8000/movies/title?title=${search}`);
  return (
    <div className="home">
      {error && <div>{error}</div>}
      {isPending && <div>Loading...</div>}
      <h2>Featured Tv Shows</h2>
      {movies && (
        <MovieList
          className="movie-center"
          movies={movies.body.filter(
            (movie) => movie.type === "series" && movie.featured === "yes"
          )}
        />
      )}
      {error && <div>{error}</div>}
      {isPending && <div>Loading...</div>}
      <h2 className="second-title">All Tv Shows</h2>
      {movies && (
        <MovieList
          movies={movies.body.filter((movie) => movie.type === "series")}
        />
      )}
    </div>
  );
};

export default TvShows;
